Here is the complete JavaScript code:
```javascript
const startCard = document.getElementById("start-card");
const endCard = document.getElementById("end-card");
const operationButtons = document.querySelectorAll(".button-container button");
const cards = document.querySelectorAll(".card:not(#start-card):not(#end-card)");

let selectedOperation = null;

// Set up click listeners for operation buttons
operationButtons.forEach(function(button) {
	button.addEventListener("click", function() {
		// Deselect previously selected operation
		if (selectedOperation !== null) {
			selectedOperation.classList.remove("selected");
		}

		// Select new operation
		selectedOperation = button;
		selectedOperation.classList.add("selected");
	});
});

// Set up click listeners for cards
cards.forEach(function(card) {
	card.addEventListener("click", handleClick);
});

function handleClick() {
	// Only perform action if an operation is selected and the card has not been used
	if (selectedOperation !== null && !this.classList.contains("used")) {
		const startValue = parseInt(startCard.querySelector("p").textContent);
		const cardValue = parseInt(this.querySelector("p").textContent);
		let result;

		// Perform arithmetic operation based on selected
		switch (selectedOperation.textContent) {
			case "+":
				result = startValue + cardValue;
				break;
			case "-":
				result = startValue - cardValue;
				break;
			case "*":
				result = startValue * cardValue;
				break;
			case "/":
				result = startValue / cardValue;
				break;
		}

		// Update start card value and clear selected operation
		startCard.querySelector("p").textContent = result;
		selectedOperation.classList.remove("selected");
		selectedOperation = null;

		// Mark card as used and clear its value
		this.classList.add("used");
		this.querySelector("p").textContent = "";

		// Add checkmark to used card
		const checkmark = document.createElement("span");
		checkmark.classList.add("checkmark");
		this.appendChild(checkmark);

		// Remove click listener from used card
		this.removeEventListener("click", handleClick);
	}
}

function generateCards() {
	// Generate random numbers and set card values
	const cardValues = [];
	for (let i = 0; i < cards.length; i++) {
		let value = Math.floor(Math.random() * 20) + 1;
		while (cardValues.includes(value)) {
			value = Math.floor(Math.random() * 20) + 1;
		}
		cardValues.push(value);

		const card = cards[i];
		const cardValue = document.createElement("p");
		cardValue.textContent = value;
		card.appendChild(cardValue);
	}
}

// Generate initial card values
generateCards();
```
