			<input type="text" id="MID" name="MID">
			<label for="Descriptor">Descriptor:</label>
			<input type="text" id="Descriptor" name="Descriptor">
		</div>
		<div id="button-container">
			<button id="add-row-button" type="button">Add Row</button>
		</div>
	</form>

	<script>
		// Select the button and the input container
		const addRowButton = document.getElementById("add-row-button");
		const inputContainer = document.querySelector(".input-container");

		// Add event listener to the button
		addRowButton.addEventListener("click", function() {
			// Clone the input container
			const clonedInputContainer = inputContainer.cloneNode(true);

			// Clear the values of the cloned input fields
			const clonedInputs = clonedInputContainer.querySelectorAll("input");
			clonedInputs.forEach(function(input) {
				input.value = "";
			});

			// Insert the cloned input container after the original input container
			inputContainer.parentNode.insertBefore(clonedInputContainer, inputContainer.nextSibling);
		});

		// Add event listener to the input fields for pasting
		const MIDInput = document.getElementById("MID");
		const DescriptorInput = document.getElementById("Descriptor");

		MIDInput.addEventListener("paste", function(event) {
			// Prevent the default paste behavior
			event.preventDefault();

			// Get the pasted data
			const pastedData = event.clipboardData.getData("text");

			// Split the pasted data into rows
			const rows = pastedData.split("\n");

			// Loop through the rows and create new input fields
			rows.forEach(function(row) {
				// Split the row into cells
				const cells = row.split("\t");

				// If the row has exactly 2 cells, create new input fields
				if (cells.length === 2) {
					// Clone the input container
					const clonedInputContainer = inputContainer.cloneNode(true);

					// Set the values of the input fields
					const clonedInputs = clonedInputContainer.querySelectorAll("input");
					clonedInputs[0].value = cells[0];
					clonedInputs[1].value = cells[1];

					// Insert the cloned input container after the original input container
					inputContainer.parentNode.insertBefore(clonedInputContainer, inputContainer.nextSibling);
				}
			});
		});
	</script>
</body>
