// Function to determine if a proposal has passed or not
function proposalResult(uint256 _proposalId) public view returns (bool) {
    Proposal storage p = proposals[_proposalId];

    if (block.timestamp <= p.votingDeadline && p.numberOfVotes < quorum) {
        return false;
    }

    return p.currentResult > 0;
}

// Function to execute a proposal
function executeProposal(uint256 _proposalId) public notExecuted(_proposalId) {
    Proposal storage p = proposals[_proposalId];

    require(proposalResult(_proposalId), "Proposal did not pass");

