function changeParameters(uint256 _quorum, uint256 _votingPeriod) public onlyOwner {
    require(_quorum > 0 && _quorum <= 100, "Quorum must be between 1 and 100");
    require(_votingPeriod > 0, "Voting period must be greater than 0");
