 if(reply:Auth-Type == "Reject"){
 update reply{
 Reply-Message := "This is the reject after checking the group name"
 }
 }else{
 update reply{
 Auth-Type := reply:Auth-Type
 Reply-Message := reply:Reply-Message
 }
 bsa\_cancel
 reject
 }
 }else{
 bsa\_cancel
 reject
 }
 }else{
 update reply{
 Reply-Message := "Unknown group name."
 }
 reject
 }
 }
