  host: 'localhost',
  user: 'root',
  password: 'password',
  database: 'smart_farming'
});

db.connect((err) => {
  if (err) {
    throw err;
  }
  console.log('Connected to database');
});

// AR Configuration
const arOptions = {
  type: 'pattern',
  patternUrl: 'https://example.com/pattern-marker.patt',
  canvasWidth: 640,
  canvasHeight: 480
};

// AR Endpoint
app.get('/ar', (req, res) => {
  const farmerId = req.query.id;
  const ar = arjs(arOptions);
  const arImage = ar.getImage();
  // Generate AR image with real-time crop data
  // TODO: integrate with real-time data from IoT sensors and ML algorithms
  res.send(arImage);
});

// Start Server
const port = 3000;
app.listen(port, () => {
  console.log(`App listening on port ${port}`);
});
